import React, { useRef, useState } from 'react';
import './ImageGenerator.css';
import default_img from '../Assets/default_img.png';

const ImageGenerator = () => {
  const [imgUrls, setImgUrls] = useState(Array(3).fill(default_img));
  const inputRef = useRef(null);
  const [loading, setLoading] = useState(false);

  const generateImages = async () => {
    if (inputRef.current.value === "") {
      return;
    }

    setLoading(true);

    try {
      const response = await fetch("https://api.openai.com/v1/images/generations", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: "Bearer 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          ",
          "User-Agent": "Chrome",
        },
        body: JSON.stringify({
          prompt: `${inputRef.current.value}`,
          n: 3,
          size: "512x512",
        }),
      });

      const data = await response.json();
      const urls = data.data.map(image => image.url);
      setImgUrls(urls);
    } catch (error) {
      console.error("Error generating images:", error);
    } finally {
      setLoading(false);
    }
  }

  return (
    <div className='ai_image_generator'>
      <div className='header'>AI Image Generator</div>
      <div className='container'>
        <div className='img_loader'>
          {imgUrls.map((url, index) => (
            <div key={index} className='image'>
              <img src={url} alt={`Generated ${index + 1}`} />
            </div>
          ))}
        </div>
        <div className='loading'>
          <div className={loading ? "loading_bar_full" : "loading_bar"}></div>
          <div className={loading ? "loading_text" : "display_none"}>Loading...</div>
        </div>
      </div>
      <div className='search_box'>
        <input type="text" ref={inputRef} className='search_input' placeholder='Describe what you want to see' />
        <div className='generate_btn' onClick={generateImages}>Generate</div>
      </div>
    </div>
  )
}

export default ImageGenerator;
